import React, { Component } from 'react';
import BuySellButtons from './components/buySellButtons';
import ComputationForm from './components/computationForm';

class App extends Component {
  //true => in buy state, false => in sell state
  state = { 
    buy_state : true,
    current_state_style : "success"
  } 

  render() { 
    return (
      <React.Fragment>

        <BuySellButtons currentState = {this.state.buy_state} changeBuyState = {this.handleChange}/>
        <ComputationForm currentState = {this.state.buy_state} currentStyle = {this.state.current_state_style}/>
        {console.log(this.state.buy_state)}

      </React.Fragment>
    );
  }

  handleChange = (newState) => {
    if (newState === this.state.buy_state){
      this.setState({buy_state : newState, current_state_style : "sucesss"});
    }

    else {
      this.setState({buy_state : newState, current_state_style : "danger"});
    }

  }
}

export default App;
