import React, { Component } from 'react';

class BuySellButtons extends Component {
    state = { 
        buy_state: true,
        buttons_state : [
            "btn btn-success", "btn btn-outline-danger"
        ]
     } 

    render() { 

        return (
            
            <React.Fragment>
                <div>
                    <button type="button" className={this.state.buttons_state[0]}>Buy</button>
                    <button type="button" className={this.state.buttons_state[1]} onClick = {this.handleSellClick}>Sell</button>
                </div>
            </React.Fragment>
            
        );
    }

    handleSellClick = () => {
        if (this.state.buy_state === false)
            return;

        console.log(this);
    }
}
 
export default BuySellButtons;
