import React, { Component } from 'react';

const default_border_style = "border border-3 rounded border-"

class ComputationForm extends Component {
    state = { 
        color : this.props.currentState ? "success" : "danger"
     } 
    render() { 
        return (
            <React.Fragment>
                <span className={default_border_style + this.state.color}>
                    ** FORM **
                </span>
            </React.Fragment>
        );
    }
}
 
export default ComputationForm;
