import React, { Component } from 'react';

const default_border_style = "border p-5 border-3 rounded-5 border-"


//className choose valid/invalid
//onChange check validation (call validation function)
    //pass the relevant element name to validation function and set error message.
//for loop through errors and output those with error

class ComputationForm extends Component {
    state = {
        values: {
            capital: "",
            risk_percentage: "",
            stock_ticker: "",
            entry_price: "",
            stop_loss: ""
        },

        computationOutput: "",

        errors: {
            capital: "",
            risk_percentage: "",
            stock_ticker: "",
            entry_price: "",
            stop_loss: "",
            logic_errors: ""
        }
    }
    
    render() {
        return (
            
            <React.Fragment>
                <div className = "d-flex align-items-center justify-content-center">
                    <border className={default_border_style + this.props.currentStyle}>
                        <div className = "form-group">
                            <div className ="mb-3">
                                <label for= "capital">Capital</label>
                                <input id="capital" 
                                name = "capital"
                                // className={"form-control " + (this.state.capital !== "" ? (e=> this.validation(e.target) ? "is-valid": "is-invalid"):"")}
                                type="text" 
                                className = {"form-control " + (this.state.errors.capital ? "is-invalid" : "")}
                                placeholder="$"
                                value={this.state.values.capital}
                                required
                                // onChange={(e)=> this.setState({capital:e.target.value})}
                                onChange={this.handleChange}
                                />
                                <div class="invalid-feedback">
                                    {this.state.errors.capital}
                                </div>
                                <div class="valid-feedback"/>
                                <small className="form-text text-muted">
                                    Your current total portfolio capital.
                                </small>
                            </div>
                            
                            <div className ="mb-3">
                                <label for= "risk_percentage">Risk Percentage</label>
                                <input id="risk_percentage" 
                                className={"form-control " + (this.state.errors.risk_percentage ? "is-invalid" : "")} 
                                name = "risk_percentage"
                                type="integer" 
                                placeholder="%"
                                value={this.state.values.risk_percentage}
                                required
                                onChange={this.handleChange}
                                />
                                <div class="invalid-feedback">
                                    {this.state.errors.risk_percentage}
                                </div>
                                <div class="valid-feedback"/>
                                <small className="form-text text-muted">
                                    Percentage of portfolio capital you are willing to risk from 0 to 100%. 
                                </small>
                            </div>

                            {/* <label for= "capital">Stock Ticker</label>
                            <input id="stock_ticker" 
                            className="form-control" 
                            type="text" 
                            placeholder="Company Name"
                            value={this.state.stock_ticker}
                            required
                            onChange={this.handleChange}
                            />
                            <small className="form-text text-muted">
                                    Your current portfolio capital.
                            </small> */}

                            <div className = "mb-3">
                                <label for= "entry_price">Entry Price</label>
                                <input id="entry_price" 
                                name = "entry_price"
                                className={"form-control " + (this.state.errors.entry_price ? "is-invalid" : "")} 
                                type="text" 
                                placeholder="$"
                                value={this.state.values.entry_price}
                                required
                                onChange={this.handleChange}
                                />
                                <div class="invalid-feedback">
                                    {this.state.errors.entry_price}
                                </div>
                                <div class="valid-feedback"/>
                            </div>

                            <div className = "mb-3">
                                <label for= "stop_loss">Stop Loss</label>
                                <input id="stop_loss" 
                                name = "stop_loss"
                                className={"form-control " + (this.state.errors.stop_loss ? "is-invalid" : "")} 
                                type="text" 
                                placeholder="$"
                                value={this.state.values.stop_loss}
                                required
                                onChange={this.handleChange}
                                />
                                <div class="invalid-feedback">
                                    {this.state.errors.stop_loss}
                                </div>
                                <div class="valid-feedback"/>
                            </div>

                            <div className='d-flex align-items-center justify-content-center mt-5'>
                                <button className={"btn btn-md m-2 rounded-pill rounded-3 btn-" + this.props.currentStyle} onClick ={this.handleComputation}>Compute</button>
                            </div>
                            
                            <p id="computationResult" className='d-flex align-items-center justify-content-center mt-3'>{this.state.computationOutput}</p>
                
                        </div>

                    </border>
                </div>
                    <p>{this.state.values.capital}</p>
                    <p>{this.state.values.risk_percentage}</p>
                    <p>{this.state.values.stock_ticker}</p>
                    <p>{this.state.values.entry_price}</p>
                    <p>{this.state.values.stop_loss}</p>
            </React.Fragment>
        );    
    }

    handleChange= (e) => {
        const values = {...this.state.values};
        values[e.target.name] = e.target.value;
        this.setState({values});
        this.validation(e.target);

        // const name = e.target.name;
        // this.setState({e.target.name: e.target.value});
        console.log(e.target);
    }

    handleComputation = () => {
        // if ((this.props.currentState === true && curr.stop_loss >= curr.entry_price) || (this.props.currentState === false && curr.stop_loss <= curr.entry_price)) return;
      
        const result = (this.state.values.capital * (this.state.values.risk_percentage/100) ) / (this.state.values.entry_price - this.state.values.stop_loss)
        this.setState({computationOutput: "Your recommended position size is " + result + " shares."});
    }

    validation({name, value}){
        const errors = {...this.state.errors};

        switch (name){
            case "capital":
                if(Number(value) != value || value < 0){
                    errors.capital = "Value must be positive.";
                    this.setState({errors});
                    return;
                }
                errors.capital = "";
                this.setState({errors});
                return;

            case "risk_percentage":
                if(Number(value) != value || value > 100 || value < 0) {
                    errors.risk_percentage = "Percentage must be between 0% and 100%.";
                    this.setState({errors});
                    return;
                }
                errors.risk_percentage = "";
                this.setState({errors});
                return;
            
            case "entry_price":
                if(Number(value) != value || value < 0){
                    errors.entry_price = "Value must be positive.";
                    this.setState({errors});
                    return;
                }
                if (this.props.currentState === true && (parseFloat(value) > parseFloat(this.state.values.stop_loss))){
                    errors.stop_loss = ""
                    this.setState({errors});
                    return;
                }
                if (this.props.currentState === true && (parseFloat(value) <= parseFloat(this.state.values.stop_loss))){
                    errors.stop_loss = "Stop loss must be less than the entry price."
                    this.setState({errors});
                    return;
                }
                if (this.props.currentState === false && (parseFloat(value) >= parseFloat(this.state.values.stop_loss))){
                    errors.stop_loss = "Stop loss must be greater than the entry price."
                    this.setState({errors});
                    return;
                }
                if (this.props.currentState === false && (parseFloat(value) < parseFloat(this.state.values.stop_loss))){
                    errors.stop_loss = ""
                    this.setState({errors});
                    return;
                }

                errors.entry_price = "";
                this.setState({errors});
                return;
            
            case "stop_loss":
                if(Number(value) != value || value < 0){
                    errors.stop_loss = "Value must be positive.";
                    this.setState({errors});
                    return;
                }
                if (this.props.currentState === true && (parseFloat(value) >= parseFloat(this.state.values.entry_price))){
                    errors.stop_loss = "Stop loss must be less than entry price."
                    this.setState({errors});
                    return;
                }
                if (this.props.currentState === true && (parseFloat(value) < parseFloat(this.state.values.entry_price))){
                    errors.stop_loss = ""
                    this.setState({errors});
                    return;
                }
                if (this.props.currentState === false && (parseFloat(value) <= parseFloat(this.state.values.entry_price))){
                    errors.stop_loss = "Stop loss must be greater than the entry price."
                    this.setState({errors});
                    return;
                }
                if (this.props.currentState === false && (parseFloat(value) > parseFloat(this.state.values.entry_price))){
                    errors.stop_loss = ""
                    this.setState({errors});
                    return;
                }

                errors.stop_loss = "";
                this.setState({errors});
                return;
        }
    }
}
 
export default ComputationForm;
