import React, { Component } from 'react';

const default_border_style = "border p-5 border-3 rounded-5 border-"

class ComputationForm extends Component {
    state = {
        capital: "",
        risk_percentage: "",
        stock_ticker: "",
        entry_price: "",
        stop_loss: ""
    }
    
    render() {
        return (
            
            <React.Fragment>
                <div className = "d-flex align-items-center justify-content-center">
                    <border className={default_border_style + this.props.currentStyle}>
                        <div className = "form-group">
                            <label for= "capital">Capital</label>
                            <input id="capital" 
                            className="form-control mb-3 " 
                            type="text" 
                            placeholder="$"
                            value={this.state.capital}
                            required
                            onChange={(e)=> this.setState({capital:e.target.value})}
                            />

                            <label for= "risk_percentage">Risk Percentage</label>
                            <input id="risk_percentage" 
                            className="form-control mb-3" 
                            type="text" 
                            placeholder="%"
                            value={this.state.risk_percentage}
                            required
                            onChange={(e)=> this.setState({risk_percentage:e.target.value})}
                            />

                            {/* <label for= "capital">Stock Ticker</label>
                            <input id="stock_ticker" 
                            className="form-control mb-3" 
                            type="text" 
                            placeholder="Company Name"
                            value={this.state.stock_ticker}
                            required
                            onChange={(e)=> this.setState({stock_ticker:e.target.value})}
                            /> */}

                            <label for= "capital">Entry Price</label>
                            <input id="entry_price" 
                            className="form-control mb-3" 
                            type="text" 
                            placeholder="$"
                            value={this.state.entry_price}
                            required
                            onChange={(e)=> this.setState({entry_price:e.target.value})}
                            />

                            <label for= "capital">Stop Loss</label>
                            <input id="stop_loss" 
                            className="form-control mb-3" 
                            type="text" 
                            placeholder="$"
                            value={this.state.stop_loss}
                            required
                            onChange={(e)=> this.setState({stop_loss:e.target.value})}
                            />    
                        </div>
                            <button className="btn btn-sm m-2 rounded-pill rounded-3 btn-warning" onClick ={this.handleComputation}>Compute</button>
                    </border>
                </div>
                    {/* <p>{this.state.capital}</p>
                    <p>{this.state.risk_percentage}</p>
                    <p>{this.state.stock_ticker}</p>
                    <p>{this.state.entry_price}</p>
                    <p>{this.state.stop_loss}</p> */}
            </React.Fragment>
        );    
    }

    handleComputation = () => {
        const curr = this.state;
        return ( curr.capital * (curr.risk_percentage/100) ) / (curr.entry_price - curr.stop_loss);
    }
}
 
export default ComputationForm;
