import React, { Component } from 'react';
import BuySellButtons from './components/buySellButtons';

class App extends Component {
  //true => in buy state, false => in sell state
  state = { 
    buy_state : true
  } 

  render() { 
    return (
      <BuySellButtons currentState = {this.state.buy_state} changeBuyState = {this.handleChange}/>
    );
  }

  handleChange = (newState) => {
    if (newState === this.state.buy_state)
      return;

    this.setState({buy_state : newState});
    console.log(this);
  }
}

export default App;
