import React, { Component } from 'react';
import * as formik from 'formik';
import * as yup from 'yup';

const default_border_style = "border p-5 border-3 rounded-5 border-"
const {Formik} = formik;

class ComputationForm extends Component {
    state = {
        capital: "",
        risk_percentage: "",
        stock_ticker: "",
        entry_price: "",
        stop_loss: "",
        computationOutput: ""
    }

    schema = yup.object().shape({
        name: yup.number().positive()
    });
    
    render() {
        return (
            
            <React.Fragment>
                <div className = "d-flex align-items-center justify-content-center">
                    <border className={default_border_style + this.props.currentStyle}>
                        <div className = "form-group">
                            <div className ="mb-3">
                                <label for= "capital">Capital</label>
                                <input id="capital" 
                                name = "capital"
                                className={"form-control " + (this.state.capital !== "" ? (this.capital_validation() ? "is-valid": "is-invalid"):"")}
                                type="text" 
                                placeholder="$"
                                value={this.state.capital}
                                required
                                onChange={(e)=> this.setState({capital:e.target.value})}
                                />
                                <div class="invalid-feedback">
                                    Value must be positive.
                                </div>
                                <div class="valid-feedback"/>
                                <small className="form-text text-muted">
                                    Your current total portfolio capital.
                                </small>
                            </div>
                            
                            <div className ="mb-3">
                                <label for= "risk_percentage">Risk Percentage</label>
                                <input id="risk_percentage" 
                                className={"form-control " + (this.state.risk_percentage !== "" ? (this.risk_percentage_validation() ? "is-valid": "is-invalid"):"")} 
                                type="integer" 
                                placeholder="%"
                                value={this.state.risk_percentage}
                                required
                                onChange={(e)=> this.setState({risk_percentage:e.target.value})}
                                />
                                <div class="invalid-feedback">
                                    Percentage must be between 0 and 100.
                                </div>
                                <div class="valid-feedback"/>
                                <small className="form-text text-muted">
                                    Percentage of portfolio capital you are willing to risk from 0 to 100%. 
                                </small>
                            </div>

                            {/* <label for= "capital">Stock Ticker</label>
                            <input id="stock_ticker" 
                            className="form-control" 
                            type="text" 
                            placeholder="Company Name"
                            value={this.state.stock_ticker}
                            required
                            onChange={(e)=> this.setState({stock_ticker:e.target.value})}
                            />
                            <small className="form-text text-muted">
                                    Your current portfolio capital.
                            </small> */}

                            <div className = "mb-3">
                                <label for= "capital">Entry Price</label>
                                <input id="entry_price" 
                                className={"form-control " + (this.state.entry_price !== "" ? (this.entry_price_validation() ? "is-valid": "is-invalid"):"")} 
                                type="text" 
                                placeholder="$"
                                value={this.state.entry_price}
                                required
                                onChange={(e)=> this.setState({entry_price:e.target.value})}
                                />
                                <div class="invalid-feedback">
                                    Value must be positive.
                                </div>
                                <div class="valid-feedback"/>
                            </div>

                            <div className = "mb-3">
                                <label for= "capital">Stop Loss</label>
                                <input id="stop_loss" 
                                className={"form-control " + (this.state.stop_loss !== "" ? (this.stop_loss_validation() ? "is-valid": "is-invalid"):"")} 
                                type="text" 
                                placeholder="$"
                                value={this.state.stop_loss}
                                required
                                onChange={(e)=> this.setState({stop_loss:e.target.value})}
                                />
                                <div class="invalid-feedback">
                                    Value must be positive.
                                </div>
                                <div class="valid-feedback"/>
                            </div>

                            {(this.props.currentState === true && this.state.stop_loss >= this.state.entry_price) || (this.props.this.stateentState === false && this.state.stop_loss <= this.state.entry_price)? <p>error</p>:<p>no error</p>}
                            
                            <div className='d-flex align-items-center justify-content-center mt-5'>
                                <button className={"btn btn-md m-2 rounded-pill rounded-3 btn-" + this.props.currentStyle} onClick ={this.handleComputation}>Compute</button>
                            </div>
                            
                            <p id="computationResult" className='d-flex align-items-center justify-content-center mt-3'>{this.state.computationOutput}</p>
                
                        </div>

                    </border>
                </div>
                    <p>{this.state.capital}</p>
                    <p>{this.state.risk_percentage}</p>
                    <p>{this.state.stock_ticker}</p>
                    <p>{this.state.entry_price}</p>
                    <p>{this.state.stop_loss}</p>
            </React.Fragment>
        );    
    }

    handleComputation = () => {
        const curr = this.state;
        if ((this.props.currentState === true && curr.stop_loss >= curr.entry_price) || (this.props.currentState === false && curr.stop_loss <= curr.entry_price)) return;

        const result = (curr.capital * (curr.risk_percentage/100) ) / (curr.entry_price - curr.stop_loss)
        this.setState({computationOutput: "Your recommended position size is " + result + " shares."});
    }

    capital_validation(){
        if(Number(this.state.capital) != this.state.capital || this.state.capital < 0) return false;
        return true;
    } 

    risk_percentage_validation(){
        if (Number(this.state.risk_percentage) != this.state.risk_percentage || this.state.risk_percentage > 100 || this.state.risk_percentage < 0) return false;
        return true; 
    } 

    entry_price_validation(){
        if(Number(this.state.entry_price) != this.state.entry_price || this.state.entry_price < 0) return false;
        return true;
    } 
    
    stop_loss_validation(){
        if(Number(this.state.stop_loss) != this.state.stop_loss || this.state.stop_loss < 0) return false;
        return true;
    } 
}
 
export default ComputationForm;
