import React, { Component } from 'react';

const default_border_style = "border border-3 rounded border-"

class ComputationForm extends Component {
    state = { 
        style_color : "success"
    } 

    render() { 
        return (
            <React.Fragment>
                <span className={default_border_style + this.getStateStyleColor}>
                    ** FORM **
                </span>
            </React.Fragment>
        );
    }

    getStateStyleColor = () => {
        if(this.props.currentState === false){
            this.setState({style_color : "danger"});
        }
    }
}
 
export default ComputationForm;
