import React, { Component } from 'react';

const default_border_style = "border p-5 border-3 rounded-5 border-"

class ComputationForm extends Component {
    state = {

    }
    
    render() { 
        return (
            
            <React.Fragment>
                <div className = "d-flex align-items-center justify-content-center">
                    <border className={default_border_style + this.props.currentStyle}>
                        <div className = "form-group">
                            <label for= "capital">Capital</label>
                            <input id="capital" className="form-control mb-3 " type="text" placeholder="Captial"/>

                            <label for= "capital">Risk Percentage</label>
                            <input id="risk_percentage" className="form-control mb-3" type="text" placeholder="%"/>

                            <label for= "capital">Stock Ticker</label>
                            <input id="stock_ticker" className="form-control mb-3" type="text" placeholder="Stock Ticker"/>

                            <label for= "capital">Entry Price</label>
                            <input id="entry_price" className="form-control mb-3" type="text" placeholder="Entry Price"/>

                            <label for= "capital">Stop Loss</label>
                            <input id="stop_loss" className="form-control mb-3" type="text" placeholder="Stop Loss"/>
                        </div>
                    </border>
                </div>
            </React.Fragment>
        );
    }
}
 
export default ComputationForm;
